import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Watermelon from './images/watermelon.jpg';
import Apple from './images/apple.jpg';
import Orange from './images/orange.jpg';
import DragonFruit from './images/dragonfruit.jpg';
import Pear from './images/pear.jpg';
import Pineapple from './images/pineapple.jpg';

function ShopContents() {
  const [toggleHeader, setToggleHeader] = useState(0);
  const [cartItems, setCartItems] = useState({
    melon: [],
    apple: [],
    orange: [],
    dragon: [],
    pear: [],
    pineapple: []
  });
  const [totalCartItems, setTotalCartItems] = useState(0);

  const inputRefs = {
    melon: useRef(),
    apple: useRef(),
    orange: useRef(),
    dragon: useRef(),
    pear: useRef(),
    pineapple: useRef()
  };

  useEffect(() => {
    sumTotal();
    updateCartDisplay();
  }, [cartItems]);

  function sumTotal() {
    let total = Object.values(cartItems).reduce((sum, items) => sum + items.length, 0);
    setTotalCartItems(total);
  }

  function updateCartDisplay() {
    const cartDisplay = document.querySelector('.cart');
    cartDisplay.innerHTML = '<h3>Cart</h3>';

    Object.entries(cartItems).forEach(([key, items]) => {
      if (items.length > 0) {
        const itemTag = document.createElement('img');
        itemTag.classList.add(`${key}Img`);
        itemTag.id = 'cartImg';
        itemTag.src = items[0];
        cartDisplay.appendChild(itemTag);

        const amount = document.createElement('div');
        amount.textContent = items.length;
        amount.classList.add(`item${key.charAt(0).toUpperCase() + key.slice(1)}Amount`);
        cartDisplay.append(amount);
      }
    });

    const counterDiv = document.querySelector('.counter');
    counterDiv.textContent = 'Total Items: ' + totalCartItems;
  }

  function addToCart(itemKey, itemSrc) {
    const itemBox = document.querySelector(`.${itemKey}Box`);
    const inputValue = parseInt(itemBox.value);
    if (inputValue > 0 && inputValue <= 10000) {
      setCartItems(prev => {
        return { ...prev, [itemKey]: Array(inputValue).fill(itemSrc) };
      });
    }
  }

  function ShowCart() {
    const cartDisplay = document.querySelector('.cart');
    const checkoutBtn = document.querySelector('.checkout');
    if (toggleHeader === 0) {
      checkoutBtn.style.display = 'flex';
      cartDisplay.style.display = 'flex';
      setToggleHeader(1);
    } else {
      setToggleHeader(0);
      checkoutBtn.style.display = 'none';
      cartDisplay.style.display = 'none';
    }
  }

  const ClearableInput = () => {
    Object.values(inputRefs).forEach(ref => (ref.current.value = ''));
  };

  const images = { Watermelon, Apple, Orange, DragonFruit, Pear, Pineapple };
  const fruitNames = ['melon', 'apple', 'orange', 'dragon', 'pear', 'pineapple'];

  return (
    <div>
      <div className='cart'>
        <h3>Cart</h3>
      </div>
      <div className='checkout'>
        <h3 className='counter'></h3>
        <button onClick={ShowCart} className='checkoutBtn'>
          Checkout
        </button>
      </div>
      <div className='header'></div>
      </div>

  )
}

  export default ShopContents
