import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Watermelon from './images/watermelon.png';
import Apple from './images/apple.png';
import Orange from './images/orange.png';
import DragonFruit from './images/dragonfruit.png';
import Pear from './images/pear.png';
import Pineapple from './images/pineapple.png';

const fruits = [
  { name: 'Watermelon', src: Watermelon, key: 'melon' },
  { name: 'Apple', src: Apple, key: 'apple' },
  { name: 'Orange', src: Orange, key: 'orange' },
  { name: 'DragonFruit', src: DragonFruit, key: 'dragon' },
  { name: 'Pear', src: Pear, key: 'pear' },
  { name: 'Pineapple', src: Pineapple, key: 'pineapple' },
];

function ShopContents() {
  let [toggleHeader, setToggleHeader] = useState(0);
  let [cartItems, setCartItems] = useState({});
  let [totalCartItems, setTotalCartItems] = useState(0);

  const inputRefs = useRef(
    fruits.reduce((acc, fruit) => {
      acc[fruit.key] = React.createRef();
      return acc;
    }, {})
  );

  function sumTotal() {
    const total = Object.values(cartItems).reduce((sum, items) => sum + items.length, 0);
    setTotalCartItems(total);
  }

  useEffect(() => {
    const counterDiv = document.querySelector('.counter');
    counterDiv.textContent = 'Total Items: ' + totalCartItems;
  }, [totalCartItems]);

  function updateCart(fruitKey, fruitSrc) {
    const existingImages = document.querySelectorAll(`.${fruitKey}Img`);
    existingImages.forEach((img) => img.remove());

    const existingAmount = document.querySelectorAll(`.item${fruitKey}Amount`);
    existingAmount.forEach((amount) => amount.remove());

    const inputVal = parseInt(inputRefs.current[fruitKey].current.value) || 0;
    const newItems = Array(inputVal).fill(fruitSrc);

    setCartItems((prev) => ({ ...prev, [fruitKey]: newItems }));
  }

  useEffect(() => {
    fruits.forEach(({ key, src }) => {
      updateCart(key, src);
    });
    sumTotal();
  }, [cartItems]);

  function toggleCart() {
    const cartDisplay = document.querySelector('.cart');
    const checkoutBtn = document.querySelector('.checkout');
    const displayValue = toggleHeader === 0 ? 'flex' : 'none';
    cartDisplay.style.display = displayValue;
    checkoutBtn.style.display = displayValue;
    setToggleHeader((prev) => 1 - prev);
  }

  function addToCart() {
    const updatedCartItems = fruits.reduce((acc, { key, src }) => {
      updateCart(key, src);
      return acc;
    }, {});

    setCartItems(updatedCartItems);
  }

  const clearInputValues = () => {
    Object.values(inputRefs.current).forEach((inputRef) => (inputRef.current.value = ''));
  };

  const [showCartPopup, setShowCartPopup] = useState(false);

  const handleCloseCartPopup = () => {
    setShowCartPopup(false);
  };

  const handleAddToCart = () => {
    addToCart();
    setShowCartPopup(true);
  };

  return (
    <div>
      {showCartPopup && (
        <div className="cart-popup">
          <div className="cart-popup-content">
            <h3>Added to Cart</h3>
            {fruits.map(({ name, key, src }) => (
              <div key={key}>
                <img className={`${key}Img`} src={src} alt={`${name}`} />
                <p className={`item${key}Amount`}>{cartItems[key]?.length || 0}</p>
              </div>
            ))}
            <button onClick={handleCloseCartPopup}>Close</button>
          </div>
        </div>
      )}
      {/* Remaining JSX code */}
      <button onClick={handleAddToCart}>Add to Cart</button>
      {/* Remaining JSX code */}
    </div>
  );
}

export default ShopContents;
