import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import Watermelon from "./images/watermelon.png";
import Apple from "./images/apple.png";
import Orange from "./images/orange.png";
import DragonFruit from "./images/dragonfruit.png";
import Pear from "./images/pear.png";
import Pineapple from "./images/pineapple.png";

function ShopContents() {
  const [toggleHeader, setToggleHeader] = useState(0);
  const [cartItems, setCartItems] = useState({
    melons: [],
    apples: [],
    oranges: [],
    dragonFruits: [],
    pears: [],
    pineapples: [],
  });
  const [totalCartItems, setTotalCartItems] = useState(0);

  const inputRefs = useRef({
    melons: useRef(),
    apples: useRef(),
    oranges: useRef(),
    dragonFruits: useRef(),
    pears: useRef(),
    pineapples: useRef(),
  });

  const fruitImages = {
    melons: Watermelon,
    apples: Apple,
    oranges: Orange,
    dragonFruits: DragonFruit,
    pears: Pear,
    pineapples: Pineapple,
  };

  function sumTotal() {
    setTotalCartItems(
      Object.values(cartItems).reduce((sum, items) => sum + items.length, 0)
    );
  }

  function updateCartDisplay() {
    const cartDisplay = document.querySelector('.cart');
    cartDisplay.innerHTML = '<h3>Cart</h3>';
    Object.entries(cartItems).forEach(([key, items]) => {
      if (items.length > 0) {
        const itemTag = document.createElement('img');
        itemTag.classList.add(`${key}Img`);
        itemTag.id = 'cartImg';
        itemTag.src = fruitImages[key];
        cartDisplay.appendChild(itemTag);

        const amount = document.createElement('div');
        amount.textContent = items.length;
        amount.classList.add(`item${key}Amount`);
        cartDisplay.append(amount);
      }
    });
  }

  useEffect(() => {
    updateCartDisplay();
    sumTotal();
  }, [cartItems]);

  function ShowCart() {
    const cartDisplay = document.querySelector('.cart');
    const checkoutBtn = document.querySelector('.checkout');
    if (toggleHeader === 0) {
      checkoutBtn.style.display = 'flex';
      cartDisplay.style.display = 'flex';
      setToggleHeader(1);
    } else {
      setToggleHeader(0);
      checkoutBtn.style.display = 'none';
      cartDisplay.style.display = 'none';
    }
  }

  function addToCart(fruit) {
    const inputValue = inputRefs.current[fruit].current.value;
    const newItems = Array.from({ length: Number(inputValue) }, () => fruitImages[fruit]);
    setCartItems((prev) => ({ ...prev, [fruit]: newItems }));
  }

  function clearInputs() {
    Object.values(inputRefs.current).forEach((inputRef) => {
      inputRef.current.value = '';
    });
  }

  function handleAddToCart() {
    Object.keys(cartItems).forEach(addToCart);
    clearInputs();
  }

  return (
    <div>
      <div className="cart">
        <h3>Cart</h3>
      </div>
      <div className="checkout">
        <h3 className="counter">Total Items: {totalCartItems}</h3>
        </div>
        </div>
        )
}

export default ShopContents;
