import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Watermelon from './images/watermelon.png';
import Apple from './images/apple.png';
import Orange from './images/orange.png';
import DragonFruit from './images/dragonfruit.png';
import Pear from './images/pear.png';
import Pineapple from './images/pineapple.png';

function ShopPage() {
  const [toggleHeader, setToggleHeader] = useState(0);
  const [cartItems, setCartItems] = useState([]);
  const [totalCartItems, setTotalCartItems] = useState(0);

  const fruitData = [
    { name: 'Watermelon', imgSrc: Watermelon, inputRef: useRef() },
    { name: 'Apple', imgSrc: Apple, inputRef: useRef() },
    { name: 'Orange', imgSrc: Orange, inputRef: useRef() },
    { name: 'DragonFruit', imgSrc: DragonFruit, inputRef: useRef() },
    { name: 'Pear', imgSrc: Pear, inputRef: useRef() },
    { name: 'Pineapple', imgSrc: Pineapple, inputRef: useRef() },
  ];

  function sumTotal() {
    setTotalCartItems(
      cartItems.reduce((total, item) => total + item.quantity, 0)
    );
  }

  useEffect(() => {
    const counterDiv = document.querySelector('.counter');
    console.log(totalCartItems);
    counterDiv.textContent = 'Total Items: ' + totalCartItems;
  }, [totalCartItems]);

  useEffect(() => {
    const cartDisplay = document.querySelector('.cart');
    cartDisplay.innerHTML = '';

    cartItems.forEach((item) => {
      const itemTag = document.createElement('img');
      itemTag.classList.add(`${item.name.toLowerCase()}Img`);
      itemTag.id = 'cartImg';
      itemTag.src = item.imgSrc;
      cartDisplay.appendChild(itemTag);

      const amount = document.createElement('div');
      amount.textContent = item.quantity;
      amount.classList.add(`item${item.name}Amount`);
      cartDisplay.append(amount);
    });
  }, [cartItems]);

  function showCart() {
    const cartDisplay = document.querySelector('.cart');
    const checkoutBtn = document.querySelector('.checkout');
    if (toggleHeader === 0) {
      checkoutBtn.style.display = 'flex';
      cartDisplay.style.display = 'flex';
      setToggleHeader((prev) => (prev = 1));
    } else {
      setToggleHeader((prev) => (prev = 0));
      checkoutBtn.style.display = 'none';
      cartDisplay.style.display = 'none';
    }
  }

  function clearCart() {
    setCartItems([]);
    showCart();
  }

  function clearableInput() {
    fruitData.forEach((fruit) => {
      fruit.inputRef.current.value = '';
    });
  }

  function addToCart() {
    const newCartItems = fruitData.map((fruit, index) => {
      const quantity = parseInt(fruit.inputRef.current.value) || 0;

      return {
        ...fruit,
        quantity,
      };
    });

    setCartItems(newCartItems);
    clearableInput();
    sumTotal();
  }

  // set props for home page with cart contents to be sent over when cart is display block on home page
  return (
    <div>
      <div className='cart'>
        <h3>Cart</h3>
      </div>
      <div className='checkout'>
        <h3 className='counter'></h3>
        <button onClick={ClearCart} className='checkoutBtn'>Checkout</button>
      </div>
      <div className='header'>
        <Link to='/shopping-cart' className='BackHome'><h3>Home</h3></Link>
        <h3 style={{color: 'black'}}>Catalog</h3>
      </div>
      <div className='cardContain'>
        <div className='card'>
          <img className='fruitPics'src={Watermelon} alt=''></img>
          <label htmlFor='numberBox'>Number of Watermelons
            <input className='melonBox' type='number' min='1' max='10' ref={inputRef} onKeyPress={(event) => {
              if (!/[1-9]/.test(event.key)) {
                event.preventDefault();
              }}}></input>
          </label>
        </div>
        <div className='card'>
          <img className='fruitPics' src={Apple} alt=''></img>
          <label htmlFor='numberBox'>Number of Apples
            <input className='appleBox' type='number' min='1' max='10' ref={inputAppleRef} onKeyPress={(event) => {
              if (!/[1-9]/.test(event.key)) {
                event.preventDefault();
              }}}></input>
          </label>
        </div>
        <div className='card'>
          <img className='fruitPics' src={Orange} alt=''></img>
          <label htmlFor='numberBox'>Number of Oranges
            <input className='orangeBox' type='number' min='1' max='10' ref={inputOrangeRef} onKeyPress={(event) => {
              if (!/[1-9]/.test(event.key)) {
                event.preventDefault();
              }}}></input>
          </label>
        </div>
        <div className='card'>
          <img className='fruitPics' src={DragonFruit} alt=''></img>
          <label htmlFor='numberBox'>Number of Dragonfruits
            <input className='dragonBox' type='number' min='1' max='10' ref={inputDragonRef} onKeyPress={(event) => {
              if (!/[1-9]/.test(event.key)) {
                event.preventDefault();
              }}}></input>
          </label>
        </div>
        <div className='card'>
          <img className='fruitPics' src={Pear} alt=''></img>
          <label htmlFor='numberBox'>Number of Pears
            <input className='pearBox' type='number' min='1' max='10' ref={inputPearsRef} onKeyPress={(event) => {
              if (!/[1-9]/.test(event.key)) {
                event.preventDefault();
              }}}></input>
          </label>
        </div>
        <div className='card'>
          <img className='fruitPics' src={Pineapple} alt=''></img>
          <label htmlFor='numberBox'>Number of Pineapples
          <input className='pineappleBox' type='number' min='1' max='10' ref={inputPineappleRef} onKeyPress={(event) => {
          if (!/[1-9]/.test(event.key)) {
            event.preventDefault();
          }}}></input>
      </label>
    </div>
    <div className='card'>
      <img className='fruitPics' src={Banana} alt=''></img>
      <label htmlFor='numberBox'>Number of Bananas
        <input className='bananaBox' type='number' min='1' max='10' ref={inputBananaRef} onKeyPress={(event) => {
          if (!/[1-9]/.test(event.key)) {
            event.preventDefault();
          }}}></input>
      </label>
    </div>
    </div>
  </div>
)}

export default ShopPage;
