import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Watermelon from './images/watermelon.png';
import Apple from './images/apple.png';
import Orange from './images/orange.png';
import DragonFruit from './images/dragonfruit.png';
import Pear from './images/pear.png';
import Pineapple from './images/pineapple.png';

// fix url switching setState saving
function ShopContents() {
  const [cartState, setCartState] = useState({
    melon: { items: [], count: 0, img: Watermelon },
    apple: { items: [], count: 0, img: Apple },
    orange: { items: [], count: 0, img: Orange },
    dragon: { items: [], count: 0, img: DragonFruit },
    pear: { items: [], count: 0, img: Pear },
    pineapple: { items: [], count: 0, img: Pineapple },
    totalCartItems: 0,
  });

  const inputRefs = {
    melon: useRef(),
    apple: useRef(),
    orange: useRef(),
    dragon: useRef(),
    pear: useRef(),
    pineapple: useRef(),
  };

  function updateCartState(fruit) {
    const count = parseInt(inputRefs[fruit].current.value);
    if (isNaN(count) || count < 1 || count > 10) return;

    const newItems = Array(count).fill(fruit);
    const updatedCartItems = [...cartState[fruit].items, ...newItems];
    const updatedCount = updatedCartItems.length;
    const totalCartItems = Object.values(cartState)
      .map((fruit) => fruit.count)
      .reduce((a, b) => a + b, 0);

    setCartState((prevState) => ({
      ...prevState,
      [fruit]: { ...prevState[fruit], items: updatedCartItems, count: updatedCount },
      totalCartItems: totalCartItems + count,
    }));
  }

  useEffect(() => {
    const counterDiv = document.querySelector('.counter');
    counterDiv.textContent = 'Total Items: ' + cartState.totalCartItems;
  }, [cartState.totalCartItems]);

  useEffect(() => {
    const cartDisplay = document.querySelector('.cart');
    cartDisplay.innerHTML = '';

    Object.entries(cartState).forEach(([fruit, fruitData]) => {
      if (fruitData.count > 0) {
        const itemTag = document.createElement('img');
        itemTag.classList.add(`${fruit}Img`);
        itemTag.id = 'cartImg';
        itemTag.src = fruitData.img;
        cartDisplay.appendChild(itemTag);

        const amount = document.createElement('div');
        amount.textContent = fruitData.count;
        amount.classList.add(`item${fruit}Amount`);
        cartDisplay.append(amount);
      }
    });
  }, [cartState]);

  // set props for home page with cart contents to be sent over when cart is display block on home page
    return (
        <div>
            <div className='cart'>
                <h3>Cart</h3>
            </div>
            <div className='checkout'>
                <h3 className='counter'></h3>
                <button onClick={ShowCart} className='checkoutBtn'>Checkout</button>
            </div>
            <div className='header'>
            <Link to='/shopping-cart' className='BackHome'><h3>Home</h3></Link>
                <h3 style={{color: 'black'}}>Catalog</h3>
            </div>
            <div className='cardContain'>
                <div className='card'>
                    <img className='fruitPics'src={Watermelon} alt=''></img>
                    <label htmlFor='numberBox'>Number of Watermelons
                      <input className='melonBox' type='number' min='1' max='10' ref={inputRef} onKeyPress={(event) => {
        if (!/[1-9]/.test(event.key)) {
          event.preventDefault();
        }}}></input>
                    </label>
                </div>
                <div className='card'>
                    <img className='fruitPics' src={Apple} alt=''></img>
                    <label htmlFor='numberBox'>Number of Apples
                      <input className='appleBox' type='number' min='1' max='10' ref={inputAppleRef} onKeyPress={(event) => {
        if (!/[1-9]/.test(event.key)) {
          event.preventDefault();
        }}}></input>
                    </label>
                </div>
                <div className='card'>
                    <img className='fruitPics' src={Orange} alt=''></img>
                    <label htmlFor='numberBox'>Number of Oranges
                      <input className='orangeBox' type='number' min='1' max='10' ref={inputOrangeRef} onKeyPress={(event) => {
        if (!/[1-9]/.test(event.key)) {
          event.preventDefault();
        }}}></input>
                    </label>
                </div>
                <div className='card'>
                    <img className='fruitPics' src={DragonFruit} alt=''></img>
                    <label htmlFor='numberBox'>Number of Dragonfruits
                      <input className='dragonBox' type='number' min='1' max='10' ref={inputDragonRef} onKeyPress={(event) => {
        if (!/[1-9]/.test(event.key)) {
          event.preventDefault();
        }}}></input>
                    </label>
                </div>
                <div className='card'>
                    <img className='fruitPics' src={Pear} alt=''></img>
                    <label htmlFor='numberBox'>Number of Pears
                      <input className='pearBox' type='number' min='1' max='10' ref={inputPearsRef} onKeyPress={(event) => {
        if (!/[1-9]/.test(event.key)) {
          event.preventDefault();
        }}}></input>
                    </label>
                </div>
                <div className='card'>
                    <img className='fruitPics' src={Pineapple} alt=''></img>
                    <label htmlFor='numberBox'>Number of Pineapples
                      <input className='pineappleBox' type='number' min='1' max='10' ref={inputPineappleRef} onKeyPress={(event) => {
        if (!/[1-9]/.test(event.key)) {
          event.preventDefault();
        }}}></input>
                    </label>
                </div>
                <div></div>
                <div className='addCartContain'>
                  <button
                    onClick={() => Object.keys(inputRefs).forEach((fruit) => updateCartState(fruit))}
                    className='addCart'
                  >
                    Add To Cart
                  </button>
                </div>
            </div>
        </div>
    )
}

export default ShopContents;
