import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Watermelon from './images/watermelon.png';
import Apple from './images/apple.png';
import Orange from './images/orange.png';
import DragonFruit from './images/dragonfruit.png';
import Pear from './images/pear.png';
import Pineapple from './images/pineapple.png';

function ShopPage() {
  const [toggleHeader, setToggleHeader] = useState(0);
  const [cartItems, setCartItems] = useState({
    watermelon: [],
    apple: [],
    orange: [],
    dragonfruit: [],
    pear: [],
    pineapple: [],
  });
  const [totalCartItems, setTotalCartItems] = useState(0);

  const images = {
    watermelon: Watermelon,
    apple: Apple,
    orange: Orange,
    dragonfruit: DragonFruit,
    pear: Pear,
    pineapple: Pineapple,
  };

  const inputRefs = {
    watermelon: useRef(),
    apple: useRef(),
    orange: useRef(),
    dragonfruit: useRef(),
    pear: useRef(),
    pineapple: useRef(),
  };

  function sumTotal() {
    let total = 0;
    for (const key in cartItems) {
      total += cartItems[key].length;
    }
    setTotalCartItems(total);
  }

  useEffect(() => {
    const counterDiv = document.querySelector('.counter');
    counterDiv.textContent = 'Total Items: ' + totalCartItems;
  }, [totalCartItems]);

  useEffect(() => {
    const cartDisplay = document.querySelector('.cart');
    cartDisplay.innerHTML = '';

    for (const key in cartItems) {
      if (cartItems[key].length > 0) {
        const itemTag = document.createElement('img');
        itemTag.classList.add(`${key}Img`);
        itemTag.id = 'cartImg';
        itemTag.src = images[key];
        cartDisplay.appendChild(itemTag);

        const amount = document.createElement('div');
        amount.textContent = cartItems[key].length;
        amount.classList.add(`item${key.charAt(0).toUpperCase() + key.slice(1)}Amount`);
        cartDisplay.append(amount);
      }
    }
  }, [cartItems]);

  function showCart() {
    const cartDisplay = document.querySelector('.cart');
    const checkoutBtn = document.querySelector('.checkout');
    if (toggleHeader === 0) {
      checkoutBtn.style.display = 'flex';
      cartDisplay.style.display = 'flex';
      setToggleHeader(1);
    } else {
      setToggleHeader(0);
      checkoutBtn.style.display = 'none';
      cartDisplay.style.display = 'none';
    }
  }

  function updateCart(key) {
    const inputValue = inputRefs[key].current.value;
    if (inputValue > 0 && inputValue <= 10000) {
      setCartItems(prev => ({ ...prev, [key]: Array.from({ length: inputValue }, () => images[key]) }));
    }
  }

  function clearCart() {
    setCartItems({
      watermelon: [],
      apple: [],
      orange: [],
      dragonfruit: [],
      pear: [],
      pineapple: [],
    });
    showCart();
  }

  function clearInputValues() {
    for (const key in inputRefs) {
      inputRefs[key].current.value = '';
    }
  }

  function handleAddToCart() {
    for (const key in inputRefs) {
      updateCart(key);
    }
    clearInputValues();
  }

  function handleKeyPress(event) {
    if (!/[1-9]/.test(event.key)) {
      event.preventDefault();
    }
  }

  return (
    <div className="shop-page">
      <header>
        <div className="counter" />
        <button className="cart-toggle" onClick={showCart}>Toggle Cart</button>
        <Link to="/checkout" className="checkout" onClick={clearCart} style={{ display: 'none' }}>Checkout</Link>
        <div className="cart" />
      </header>

      <div className="shop-content">
        {Object.keys(images).map(key => (
          <div className="item" key={key}>
            <img src={images[key]} alt={key} />
            <div className="item-info">
              <h3>{key.charAt(0).toUpperCase() + key.slice(1)}</h3>
              <input
                type="text"
                ref={inputRefs[key]}
                onKeyPress={handleKeyPress}
                placeholder="Enter quantity (1-10000)"
              />
              <button onClick={() => updateCart(key)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>

      <div className="clear-inputs">
        <button onClick={clearInputValues}>Clear Input Values</button>
      </div>
      <div className="add-to-cart">
        <button onClick={handleAddToCart}>Add All to Cart</button>
      </div>
    </div>
  );
}

export default ShopPage;
