import './App.css';
import Shelf from './images/shelf.jpg';
import { Link } from 'react-router-dom'
import { useState } from 'react';
import { useEffect } from 'react';
//import ShopPage from './ShopPage.js';

function App() {

  function ShowCart() {
    let [shown, nextVal] = useState(0);

    useEffect(() => {
    const cartDisplay = document.querySelector('.cart');
    if (shown === 0) {
      cartDisplay.style.display = 'block';
      nextVal(prev => prev.concat(1));
    }
    else {
      nextVal(prev => prev.concat(0));
      cartDisplay.style.display = 'none';
    }
  })
  }

  return (
    <div>
      <div className='cart'>
        <div>hello</div>
      </div>
      <div className='header'>
        <h2>Fruits</h2>
        <Link className='homeShop' to='/shop'> <h2>Shop</h2></Link>
        <h2 className='cartBtn' onClick={ShowCart}>Cart</h2>
      </div>
      <div className='info'>
        <div className='slogan'>Check out this wide assortment of fruits! <img className='shelf' alt='' src={Shelf}></img></div>
        <Link to='/shop' className='shopBtn'>SHOP</Link>
      </div>
    </div>
  );
}

export default App;
